CREATE OR REPLACE FUNCTION dfh.sf_make_salseinfo()
RETURNS void AS $$
DECLARE
	datas record;
BEGIN
    for datas in (	select B.sale,A.bhno, A.cseq, B.confirm, A.att, B.name from (
											select MAX(bhno)as bhno, MAX(cseq)as cseq, att from dfh.tb_customer_attribute where amode = '14' GROUP BY att) A
										LEFT JOIN (select sale,branch,cesq,confirm,name as name from dfh.tb_customerinfo) B on A.bhno = B.branch and  A.cseq = B.cesq
										where A.att not in ( select tlno from dfh.tb_salseinfo ) ) loop

																					'4','0','1','0',now() at time zone 'CCT',NULL,datas.name,'postgres');
    end loop;

END;
$$
LANGUAGE plpgsql

